namespace SolubleTest\Japha\Bridge;

use PHPUnit\Framework\Attributes\Test;
use Soluble\Japha\Bridge\Adapter;
use PHPUnit\Framework\TestCase;
use DMS\PHPUnitExtensions\ArraySubset\ArraySubsetAsserts;

/**
 * Generated by PHPUnit_SkeletonGenerator on 2014-11-04 at 16:47:42.
 */
class AdapterUsageUtilTest extends TestCase
{
    use ArraySubsetAsserts;

    /**
     * @var string
     */
    protected $servlet_address;

    /**
     * @var Adapter
     */
    protected $adapter;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp(): void
    {
        \SolubleTestFactories::startJavaBridgeServer();

        $this->servlet_address = \SolubleTestFactories::getJavaBridgeServerAddress();

        $this->adapter = new Adapter([
            'driver' => 'Pjb62',
            'servlet_address' => $this->servlet_address,
        ]);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown(): void
    {
    }

    #[Test]
    public function hashSet(): void
    {
        $ba = $this->adapter;
        $set = $ba->java('java.util.HashSet');
        $elements = ['one', 'two', 'three'];
        foreach ($elements as $element) {
            $set->add($element);
        }
        
        $this->assertCount($set->size(), $elements);
        $this->assertTrue($set->contains('one'));
        $this->assertFalse($set->contains('four'));
    }

    #[Test]
    public function treeSet(): void
    {
        $ba = $this->adapter;
        $set = $ba->java('java.util.HashSet');
        $numbers = [12, 34, 200, 8, 24];
        foreach ($numbers as $number) {
            $set->add($number);
        }
        
        $treeSet = $ba->java('java.util.TreeSet', $set);
        $this->assertSame(8, $treeSet->first()->intValue());
        $this->assertSame(200, $treeSet->last()->intValue());
    }

    #[Test]
    public function enum(): void
    {
        $ba = $this->adapter;
        $formatStyle = $ba->javaClass('java.time.format.FormatStyle');

        $this->assertSame('LONG', $formatStyle->LONG);
        $this->assertNotSame('LONG', $formatStyle->SHORT);

        $availableStyles = $formatStyle->values();
        $styles = [];
        foreach ($availableStyles as $style) {
            $styles[] = (string) $style->toString();
        }
        
        $this->assertArraySubset(['FULL', 'LONG', 'MEDIUM', 'SHORT'], $styles);
    }

    #[Test]
    public function enumSet(): void
    {
        $ba = $this->adapter;
        $formatStyle = $ba->javaClass('java.time.format.FormatStyle');

        $enumSet = $ba->javaClass('java.util.EnumSet');
        $set = $enumSet->of($formatStyle->LONG);
        $this->assertSame('[LONG]', (string) $set->toString());

        $sets = $enumSet->of($formatStyle->LONG, $formatStyle->SHORT);
        $this->assertSame('[LONG, SHORT]', (string) $sets->toString());
    }
}
